package main

import "github.com/romanthekat/coding-challenges/LeetCode/common"

/**
Given a string s, find the longest palindromic substring in s. You may assume that the maximum length of s is 1000.

Example 1:

Input: "babad"
Output: "bab"
Note: "aba" is also a valid answer.

Example 2:

Input: "cbbd"
Output: "bb"
*/
func main() {
	common.AssertEqual(longestPalindrome("bb"), "bb")
	common.AssertEqual(longestPalindrome("babad"), "bab")
	common.AssertEqual(longestPalindrome("cbbd"), "bb")
	common.AssertEqual(longestPalindrome("a"), "a")
	common.AssertEqual(longestPalindrome(""), "")
}

func longestPalindrome(s string) string {
	if len(s) <= 1 {
		return s
	}

	topLength := 0
	topPalindrome := ""

	for i := 0; i < len(s); i++ {
		for j := len(s); j >= i+1; j-- {
			possibleLength := j - i
			if possibleLength < topLength {
				break
			}

			if isPalindrome(s[i:j]) && possibleLength > topLength {
				topLength = possibleLength
				topPalindrome = s[i:j]
			}
		}
	}

	return topPalindrome
}

func isPalindrome(s string) bool {
	runes := []rune(s)
	for i := 0; i < len(s)/2; i++ {
		if runes[i] != runes[len(s)-i-1] {
			return false
		}
	}

	return true
}
